import AddIcon from '@mui/icons-material/Add';
import { IconButton } from '@mui/material';


const API_URL = 'http://localhost:5000/api';

const ProfileInfo = ({ user }) => (
    <div className="detail-card">
        <h1 className="detail-title">{user?.firstName || ''} {user?.lastName || ''}</h1>
        <div className="detail-content">
            <p>Email: {user?.email || 'Не указан'}</p>
            <p>Телефон: {user?.phone || 'Не указан'}</p>
            <p>Дата регистрации: {new Date(user?.createdAt).toLocaleDateString()}</p>
        </div>
    </div>
);

const AdminInfo = ({ user }) => {
    if (user?.role !== 'admin') return null;
    
    return (
        <div className="detail-card">
            <h3 className="detail-title">Дополнительная информация</h3>
            <div className="detail-content">
                <p>Статус: {user?.status}</p>
                <p>Последний вход: {user?.lastLogin ? new Date(user?.lastLogin).toLocaleString() : 'Не указано'}</p>
            </div>
        </div>
    );
};

const Sells = () => {
    const [sales, setSales] = useState([]);

    return (
        <div className="detail-card">
            <h3 className="detail-title">Продажи</h3>
            <div className="detail-content">
                {sales.length === 0 ? (
                    <p>У вас пока нет продаж</p>
                ) : (
                    <div className="sales-history">
                        {sales.map(sale => (
                            <div key={sale.id} className="sale-item">
                                <div className="sale-header">
                                    <span>Продажа №{sale.id}</span>
                                    <span>{sale.date.toLocaleDateString()}</span>
                                </div>
                                <div className="sale-details">
                                    {sale.items.map(item => (
                                        <div key={item.name} className="sale-item-detail">
                                            <span>{item.name}</span>
                                            <span>Количество: {item.quantity}</span>
                                            <span>{item.price} ₽</span>
                                        </div>
                                    ))}
                                </div>
                                <div className="sale-total">
                                    Итого: {sale.total} ₽
                                </div>
                            </div>
                        ))}
                    </div>
                )}
            </div>
        </div>
    );
};

const Catalog = () => {
    const [products, setProducts] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [isAddingProduct, setIsAddingProduct] = useState(false);
    const [newProduct, setNewProduct] = useState({
        name: '',
        description: '',
        price: ''
    });

    useEffect(() => {
        const fetchProducts = async () => {
            try {
                const response = await axios.get(`${API_URL}/profile`);
                setProducts(response.data);
            } catch (error) {
                console.error('Ошибка при загрузке продуктов:', error);
                setError('Ошибка при загрузке продуктов');
            }
        };

        fetchProducts();
    }, []);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setNewProduct(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post(`${API_URL}/profile`, newProduct);
            setProducts([...products, response.data]);
            setIsAddingProduct(false);
            setNewProduct({ name: '', description: '', price: '' });
        } catch (error) {
            console.error('Ошибка при добавлении продукта:', error);
        }
    };

    const handleDelete = async (productId) => {
        try {
            await axios.delete(`${API_URL}/profile/${productId}`);
            setProducts(products.filter(product => product.id !== productId));
        } catch (error) {
            console.error('Ошибка при удалении продукта:', error);
        }
    };

    return (
        <div>
            <div className="catalog-header">
                <h2 className='ctl'>Каталог продуктов  <AddIcon className="add"onClick={() => setIsAddingProduct(!isAddingProduct)}/></h2>        
            </div>

            {isAddingProduct ? (
                <form onSubmit={handleSubmit} className="add-product-form">
                    <input
                        type="text"
                        name="name"
                        value={newProduct.name}
                        onChange={handleInputChange}
                        placeholder="Название товара"
                        required
                    />
                    <textarea
                        name="description"
                        value={newProduct.description}
                        onChange={handleInputChange}
                        placeholder="Описание товара"
                        required
                    />
                    <input
                        type="number"
                        name="price"
                        value={newProduct.price}
                        onChange={handleInputChange}
                        placeholder="Цена"
                        required
                    />
                    <button type="submit">Сохранить товар</button>
                </form>
            ) : (
                <div className="product-list">
                    {products.length === 0 ? (
                        <p>Нет доступных продуктов.</p>
                    ) : (
                        products.map((product) => (
                            <div key={product.id} className="product-item">
                                <div className="product-content">
                                    <h3>{product.name}</h3>
                                    <p>{product.description}</p>
                                    <p>Цена: {product.price} ₽</p>
                                </div>
                                <div className="product-actions">
                                    <IconButton className="icon-button edit">
                                        <EditIcon />
                                    </IconButton>
                                    <IconButton className="icon-button delete" onClick={() => handleDelete(product.id)}>
                                        <DeleteIcon />
                                    </IconButton>
                                </div>
                            </div>
                        ))
                    )}
                </div>
            )}
        </div>
    );
};


const Orders = ({ user }) => {
    const [orders, setOrders] = useState([]);
    return (
        <div className="detail-card">
            <h3 className="detail-title">Заказы</h3>
            <div className="detail-content">
                {orders.length === 0 ? (
                    <p>У вас пока нет заказов</p>
                ) : (
                    <div className="order-history">
                        {orders.map(order => (
                            <div key={order.id} className="order-item">
                                <div className="order-header">
                                    <span>Заказ №{order.id}</span>
                                    <span>{order.date.toLocaleDateString()}</span>
                                    <span className={`order-status ${order.status.toLowerCase()}`}>
                                        {order.status}
                                    </span>
                                </div>
                                <div className="order-details">
                                    {order.items.map(item => (
                                        <div key={item.name} className="order-item-detail">
                                            <span>{item.name}</span>
                                            <span>Количество: {item.quantity}</span>
                                            <span>{item.price} ₽</span>
                                        </div>
                                    ))}
                                </div>
                                <div className="order-total">
                                    Итого: {order.total} ₽
                                </div>
                            </div>
                        ))}
                    </div>
                )}
            </div>
        </div>
    );
};


const Profile = () => {
    const { user } = useAuth();
    const [activeView, setActiveView] = useState('profile');
    const [products, setProducts] = useState([]);
    const handleAddProduct = (newProduct) => {
        setProducts((prevProducts) => [...prevProducts, newProduct]);
    };

    const renderContent = () => {
        switch(activeView) {
            case 'profile':
                return (
                    <div className='profile-details'>
                        <ProfileInfo user={user} />
                        <AdminInfo user={user} />
                    </div>
                );
            case 'addproduct':
                return <AddProductForm onAdd={handleAddProduct}/>;
            case 'catalog':
                return <Catalog />;
            case 'sells':
                return <Sells />;
            case 'orders':
                return <Orders />;
            default:
                return null;
        }
    };

    return (
        <div>
            <Navbar setActiveView={setActiveView} />
            <div className="profile-container">
                <div className="profile-details">
                    {renderContent()}
                </div>
            </div>
        </div>
    );
};

export default Profile;
