import React, { useState, useEffect } from "react";
import "../styles/dashboard.css";
import "../styles/global.css";
import axios from "axios";

const Catalog = () => {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/profile");
        setProducts(response.data);
      } catch (error) {
        console.error("Ошибка при загрузке продуктов:", error);
        setError("Ошибка при загрузке продуктов");
      } finally {
      }
    };
    fetchProducts();
  }, []);
  
  return (
    <main>
      {/* Каталог */}
      <div className="catalog-container">
        <div className="title-primary">Каталог</div>

        {products.length === 0 ? (
          <p>Нет доступных продуктов.</p>
        ) : (
          <div className="catalog-grid">
            {products.map((product) => (
              <div key={product.id} className="card-catalog">
                {/* Изображение продукта */}
                <div className="card-img"  style={{
    backgroundImage: product.image
      ? `url(http://localhost:5000/${product.image})`
      : "url(https://via.placeholder.com/150)",
    backgroundSize: "cover",
    backgroundPosition: "center",
  }}></div>

                {/* Название продукта */}
                <div className="card-title">{product.name}</div>

                {/* Описание продукта */}
                <div className="card-subtitle">{product.description}</div>

                {/* Цена продукта */}
                <div className="card-price">{product.price} руб</div>

                {/* Кнопка покупки */}
                <button className="card-btn">Купить</button>
              </div>
            ))}
          </div>
        )}
      </div>
    </main>
  );
};

export default Catalog;
