import React, { useState, useEffect  } from 'react';

import { useAuth } from '../components/Auth/Auth';
import Navbar from '../components/UI/NavbarProfile';
import '../styles/dashboard.css';
import '../styles/global.css';

const AddProduct = () => {
    const [name, setName] = useState('');
    const [price, setPrice] = useState('');
    const [quantity, setQuantity] = useState('');
    const [description, setDescription] = useState('');

    const handleSubmit = async (event) => {
        event.preventDefault();
        const productData = { name, price, quantity, description };

        try {
            const response = await fetch('/api/products', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(productData),
            });

            if (response.ok) {
                const newProduct = await response.json();
                console.log('Product added:', newProduct);
                // Очистить форму или выполнить другие действия
            } else {
                console.error('Error adding product:', response.statusText);
            }
        } catch (error) {
            console.error('Error:', error);
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <h3>Добавить товар</h3>
            <input
                type="text"
                placeholder="Название товара"
                value={name}
                onChange={(e) => setName(e.target.value)}
                required
            />
            <input
                type="number"
                placeholder="Цена"
                value={price}
                onChange={(e) => setPrice(e.target.value)}
                required
            />
            <input
                type="number"
                placeholder="Количество"
                value={quantity}
                onChange={(e) => setQuantity(e.target.value)}
                required
            />
            <textarea
                placeholder="Описание"
                value={description}
                onChange={(e) => setDescription(e.target.value)}
            />
            <button type="submit">Добавить товар</button>
        </form>
    );
};


const ProfileInfo = ({ user }) => (
    <div className="detail-card">
        <h1 className="detail-title">{user?.firstName || ''} {user?.lastName || ''}</h1>
        <div className="detail-content">
            <p>Email: {user?.email || 'Не указан'}</p>
            <p>Телефон: {user?.phone || 'Не указан'}</p>
            <p>Дата регистрации: {new Date(user?.createdAt).toLocaleDateString()}</p>
        </div>
    </div>
);
const AdminInfo = ({ user }) => {
    if (user?.role !== 'admin') return null;
    
    return (
        <div className="detail-card">
            <h3 className="detail-title">Дополнительная информация</h3>
            <div className="detail-content">
                <p>Статус: {user?.status}</p>
