const Profile = () => {
    const { user } = useAuth();
    const [activeView, setActiveView] = useState('profile');

    const renderContent = () => {
        switch(activeView) {
            case 'profile':
                return (
                    <div className='profile-details'>
                        <ProfileInfo user={user} />
                        <AdminInfo user={user} />
                    </div>
                );
            case 'addproduct':
                return <AddProduct />;
            case 'catalog':
                return <Catalog />;
            case 'sells':
                return <Sells />;
            case 'orders':
                return <Orders />;
            default:
                return null;
        }
    };

    return (
        <div>
            <Navbar setActiveView={setActiveView} />
            <div className="profile-container">
                <div className="profile-details">
                    {renderContent()}
                </div>
            </div>
        </div>
    );
};

export default Profile;
