import React, { useState, useEffect  } from 'react';

import { useAuth } from '../components/Auth/Auth';
import Navbar from '../components/UI/NavbarProfile';
import '../styles/dashboard.css';
import '../styles/global.css';


const ProfileInfo = ({ user }) => (
    <div className="detail-card">
        <h1 className="detail-title">{user?.firstName || ''} {user?.lastName || ''}</h1>
        <div className="detail-content">
            <p>Email: {user?.email || 'Не указан'}</p>
            <p>Телефон: {user?.phone || 'Не указан'}</p>
            <p>Дата регистрации: {new Date(user?.createdAt).toLocaleDateString()}</p>
        </div>
    </div>
);
const AdminInfo = ({ user }) => {
    if (user?.role !== 'admin') return null;
    
    return (
        <div className="detail-card">
            <h3 className="detail-title">Дополнительная информация</h3>
            <div className="detail-content">
                <p>Статус: {user?.status}</p>
                <p>Последний вход: {user?.lastLogin ? new Date(user?.lastLogin).toLocaleString() : 'Не указано'}</p>
            </div>
        </div>
    );
};

const Sells = () => {
    const [sales, setSales] = useState([]);

    return (
        <div className="detail-card">
            <h3 className="detail-title">Продажи</h3>
            <div className="detail-content">
                {sales.length === 0 ? (
                    <p>У вас пока нет продаж</p>
                ) : (
                    <div className="sales-history">
                        {sales.map(sale => (
                            <div key={sale.id} className="sale-item">
                                <div className="sale-header">
                                    <span>Продажа №{sale.id}</span>
                                    <span>{sale.date.toLocaleDateString()}</span>
                                </div>
                                <div className="sale-details">
                                    {sale.items.map(item => (
                                        <div key={item.name} className="sale-item-detail">
                                            <span>{item.name}</span>
                                            <span>Количество: {item.quantity}</span>
                                            <span>{item.price} ₽</span>
                                        </div>
                                    ))}
                                </div>
                                <div className="sale-total">
                                    Итого: {sale.total} ₽
                                </div>
                            </div>
                        ))}
                    </div>
                )}
            </div>
        </div>
    );
};

const Catalog = () => {
    const [products, setProducts] = useState([]);

    useEffect(() => {
        const fetchProducts = async () => {
            try {
                const mockProducts = [
                    { id: 1, name: 'Delrone Classic', price: 6000 },
                    { id: 2, name: 'Delrone Premium', price: 15000 }
                ];
                setProducts(mockProducts);
            } catch (error) {
                console.error('Ошибка загрузки каталога', error);
            }
        };

        fetchProducts();
    }, []);

    return (
        <div className="detail-card">
            <h3 className="detail-title">Каталог</h3>
            <div className="detail-content">
                {products.length === 0 ? (
                    <p>Каталог пуст</p>
                ) : (
                    <div className="product-list">
                        {products.map(product => (
                            <div key={product.id} className="product-item">
                                <span>{product.name}</span>
                                <span>{product.price} ₽</span>
                            </div>
                        ))}
                    </div>
                )}
            </div>
        </div>
    );
};

const Orders = ({ user }) => {
    const [orders, setOrders] = useState([]);
    useEffect(() => {
        const fetchOrders = async () => {
            try {
                const mockOrders = [
                    {
                        id: 1,
                        date: new Date('2023-06-15'),
                        total: 5999,
                        status: 'Доставлен',
                        items: [
                            { name: 'Delrone Classic', quantity: 1, price: 5999 }
                        ]
                    },
                    {
                        id: 2,
                        date: new Date('2023-07-20'),
                        total: 7999,
                        status: 'В процессе',
                        items: [
                            { name: 'Delrone Premium', quantity: 1, price: 7999 }
                        ]
                    }
                ];
                setOrders(mockOrders);
            } catch (error) {
                console.error('Ошибка загрузки истории заказов', error);
            }
        };

        fetchOrders();
    }, [user]);

    return (
        <div className="detail-card">
            <h3 className="detail-title">Заказы</h3>
            <div className="detail-content">
                {orders.length === 0 ? (
                    <p>У вас пока нет заказов</p>
                ) : (
                    <div className="order-history">
                        {orders.map(order => (
                            <div key={order.id} className="order-item">
                                <div className="order-header">
                                    <span>Заказ №{order.id}</span>
                                    <span>{order.date.toLocaleDateString()}</span>
                                    <span className={`order-status ${order.status.toLowerCase()}`}>
                                        {order.status}
                                    </span>
                                </div>
                                <div className="order-details">
                                    {order.items.map(item => (
                                        <div key={item.name} className="order-item-detail">
                                            <span>{item.name}</span>
                                            <span>Количество: {item.quantity}</span>
                                            <span>{item.price} ₽</span>
                                        </div>
                                    ))}
                                </div>
                                <div className="order-total">
                                    Итого: {order.total} ₽
                                </div>
                            </div>
                        ))}
                    </div>
                )}
            </div>
        </div>
    );
};


const Profile = () => {
    const { user } = useAuth();
    const [activeView, setActiveView] = useState('profile');

    const renderContent = () => {
        switch(activeView) {
            case 'profile':
                return (
                    <div className='profile-details'>
                        <ProfileInfo user={user} />
                        <AdminInfo user={user} />
                    </div>
                );
            case 'catalog':
                return <Catalog />;
            case 'sells':
                return <Sells />;
            case 'orders':
                return <Orders />;
            default:
                return null;
        }
    };

    return (
        <div>
            <Navbar setActiveView={setActiveView} />
            <div className="profile-container">
                <div className="profile-details">
                    {renderContent()}
                </div>
            </div>
        </div>
    );
};

export default Profile;
