import React, { useState, useEffect  } from 'react';
import axios from 'axios';
import { useAuth } from '../components/Auth/Auth';
import Navbar from '../components/UI/NavbarProfile';
import '../styles/dashboard.css';
import '../styles/global.css';

const AddProduct = ({ user }) => {
    const [productName, setProductName] = useState('');
    const [productDescription, setProductDescription] = useState('');
    const [productPrice, setProductPrice] = useState('');
    const [productImage, setProductImage] = useState(null); // State for the image

    const handleImageChange = (e) => {
        const file = e.target.files[0];
        if (file) {
            setProductImage(URL.createObjectURL(file)); // Preview the image
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (productName && productPrice) {
            const newProduct = {
                name: productName,
                description: productDescription,
                price: parseFloat(productPrice),
                image: productImage // Include the image in the product data
            };

            // Send the new product data to the server
            try {
                const response = await fetch('/api/profile', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify(newProduct),
                });

                if (response.ok) {
                    // Handle successful response
                    alert('Товар добавлен успешно!');
                    // Reset form fields
                    setProductName('');
                    setProductDescription('');
                    setProductPrice('');
                    setProductImage(null);
                } else {
                    // Handle error response
                    alert('Ошибка при добавлении товара');
                }
            } catch (error) {
                console.error('Ошибка:', error);
                alert('Ошибка при добавлении товара');
            }
        }
    };

    return (
        <>
            <div className="detail-card">
                <h3 className="detail-title">Добавить товар</h3>
                <form onSubmit={handleSubmit} style={{ marginTop: '20px' }}>
                    <div className="form-group">
                        <label>Загрузить изображение:</label>
                        <input
                            type="file"
                            accept="image/*"
                            onChange={handleImageChange}
                        />
                    </div>
                    {productImage && <img src={productImage} alt="Preview" style={{ width: '100px', height: '100px', marginTop: '10px' }} />}
                    <div className="form-group">
                        <label>Название товара:</label>
                        <input
                            type="text"
                            value={productName}
                            onChange={(e) => setProductName(e.target.value)}
                            required
                        />
                    </div>
                    <div className="form-group">
                        <label>Описание товара:</label>
                        <textarea
                            value={productDescription}
                            onChange={(e) => setProductDescription(e.target.value)}
                        />
                    </div>
                    <div className="form-group">
                        <label>Цена товара:</label>
                        <input
                            type="number"
                            value={productPrice}
                            onChange={(e) => setProductPrice(e.target.value)}
                            required
                        />
                    </div>
                    <button type="submit" className="add-product-button">Добавить товар</button>
                </form>
            </div>
        </>
    );
};


const ProfileInfo = ({ user }) => (
    <div className="detail-card">
        <h1 className="detail-title">{user?.firstName || ''} {user?.lastName || ''}</h1>
        <div className="detail-content">
            <p>Email: {user?.email || 'Не указан'}</p>
            <p>Телефон: {user?.phone || 'Не указан'}</p>
            <p>Дата регистрации: {new Date(user?.createdAt).toLocaleDateString()}</p>
        </div>
    </div>
);
const AdminInfo = ({ user }) => {
    if (user?.role !== 'admin') return null;
    
    return (
        <div className="detail-card">
            <h3 className="detail-title">Дополнительная информация</h3>
            <div className="detail-content">
                <p>Статус: {user?.status}</p>
                <p>Последний вход: {user?.lastLogin ? new Date(user?.lastLogin).toLocaleString() : 'Не указано'}</p>
            </div>
        </div>
    );
};

const Sells = () => {
    const [sales, setSales] = useState([]);

    return (
        <div className="detail-card">
            <h3 className="detail-title">Продажи</h3>
            <div className="detail-content">
                {sales.length === 0 ? (
                    <p>У вас пока нет продаж</p>
                ) : (
                    <div className="sales-history">
                        {sales.map(sale => (
                            <div key={sale.id} className="sale-item">
                                <div className="sale-header">
                                    <span>Продажа №{sale.id}</span>
                                    <span>{sale.date.toLocaleDateString()}</span>
                                </div>
                                <div className="sale-details">
                                    {sale.items.map(item => (
                                        <div key={item.name} className="sale-item-detail">
                                            <span>{item.name}</span>
                                            <span>Количество: {item.quantity}</span>
                                            <span>{item.price} ₽</span>
                                        </div>
                                    ))}
                                </div>
                                <div className="sale-total">
                                    Итого: {sale.total} ₽
                                </div>
                            </div>
                        ))}
                    </div>
                )}
            </div>
        </div>
    );
};

const Catalog = () => {
    const [products, setProducts] = useState([]);
    return (
        <div className="detail-card">
            <h3 className="detail-title">Каталог</h3>
            <div className="detail-content">
                {products.length === 0 ? (
                    <p>Каталог пуст</p>
                ) : (
                    <div className="product-list">
                        {products.map(product => (
                            <div key={product.id} className="product-item">
                                <span>{product.name}</span>
                                <span>{product.price} ₽</span>
                            </div>
                        ))}
                    </div>
                )}
            </div>
        </div>
    );
};

const Orders = ({ user }) => {
    const [orders, setOrders] = useState([]);
    return (
        <div className="detail-card">
            <h3 className="detail-title">Заказы</h3>
            <div className="detail-content">
                {orders.length === 0 ? (
                    <p>У вас пока нет заказов</p>
                ) : (
                    <div className="order-history">
                        {orders.map(order => (
                            <div key={order.id} className="order-item">
                                <div className="order-header">
                                    <span>Заказ №{order.id}</span>
                                    <span>{order.date.toLocaleDateString()}</span>
                                    <span className={`order-status ${order.status.toLowerCase()}`}>
                                        {order.status}
                                    </span>
                                </div>
                                <div className="order-details">
                                    {order.items.map(item => (
                                        <div key={item.name} className="order-item-detail">
                                            <span>{item.name}</span>
                                            <span>Количество: {item.quantity}</span>
                                            <span>{item.price} ₽</span>
                                        </div>
                                    ))}
                                </div>
                                <div className="order-total">
                                    Итого: {order.total} ₽
                                </div>
                            </div>
                        ))}
                    </div>
                )}
            </div>
        </div>
    );
};


const Profile = () => {
    const { user } = useAuth();
    const [activeView, setActiveView] = useState('profile');

    const renderContent = () => {
        switch(activeView) {
            case 'profile':
                return (
                    <div className='profile-details'>
                        <ProfileInfo user={user} />
                        <AdminInfo user={user} />
                    </div>
                );
            case 'addproduct':
                return <AddProduct />;
            case 'catalog':
                return <Catalog />;
            case 'sells':
                return <Sells />;
            case 'orders':
                return <Orders />;
            default:
                return null;
        }
    };

    return (
        <div>
            <Navbar setActiveView={setActiveView} />
            <div className="profile-container">
                <div className="profile-details">
                    {renderContent()}
                </div>
            </div>
        </div>
    );
};

export default Profile;
